import React, { Component } from "react";
import "./App.css";
// component
import CommentBox from "./CommentBox.js";
// import RecommendButton from "./RecommendButton.js";
// json
import _commentsJson from "./data/comments.json";
// function
import * as Myfunc from "./Myfunc.js";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      commentsJson: _commentsJson,
    };
  }
  render() {
    var comment_list = Myfunc.loadcommentsJsonToList(this.state._commentsJson);
    return (
      <div id="CommentArea">{comment_list}</div>

      // <div id='grid_container'>
      //   <div></div>
      // </div>
    );
  }
}

export default App;
