import React from "react";
class RecommendButton extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    if (this.props.upVote) {
      return <button className="voteUp" onClick={this.handleOnClick}>üëç{this.props.recommend}</button>;
    } else if (this.props.downVote) {
      return <button className="voteDown" onClick={this.handleOnClick}>üëé{this.props.recommend}</button>;
    }
  }

  handleOnClick = () => {
    this.props.onRecommend();
  };
}

export default RecommendButton;
