import React, { Component } from "react";

class CommentBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      _isReadOnly: this.props.isReadonly, // true: 읽기전용
      _content: "" + this.props.content, // 문장 내용
    };
  }

  render() {
    if (this.state._isReadOnly) {
      return <input type="text" value={this.state._content} readOnly={true}></input>;
    } else {
      return (
        <input
          type="text"
          value={this.state._content}
          onChange={(e) => {
            this.setState((this.state._content = this.state._content + e.target.value));
          }}
        ></input>
      );
    }
  }
}

export default CommentBox;
