import React, { Component } from "react";

class CommentBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      _isReadOnly: this.props.isReadOnly, // true: 읽기전용
      _content: "" + this.props.content, // 문장 내용
    };
  }

  render() {
    if (this.state._isReadOnly) {
      // 읽기전용
      console.log("True");
      return <input type="text" defaultValue={this.state._content} readOnly={false}></input>;
    } else {
      // 읽기전용이 아닌 경우
      console.log("False");
      return (
        <input
          type="text"
          value={this.state._content}
          onChange={function (e) {
            this.setState({ _content: this.state._content + e.target.value });
          }.bind(this)}
        ></input>
      );
    }
  }
}

export default CommentBox;
