import React, { Component } from "react";
import "./App.css";
// component
import CommentBox from "./CommentBox.js";
// import RecommendButton from "./RecommendButton.js";
// json
import commentsJson from "./data/comments.json";
// function
import * as Myfunc from "./Myfunc.js";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      _commentsJson: commentsJson,
    };
  }
  render() {
    var comment_list = Myfunc.loadcommentsJsonToList(this.state._commentsJson);
    return (
      <div id="CommentArea">
        {/* {comment_list} */}
        <CommentBox isReadOnly={true}></CommentBox>
      </div>
    );
  }
}

export default App;
