import React from "react";
import Sentence from "./Sentence";
import "./ReadSentenceArea.css";

class ReadSentenceArea extends React.Component {
  render() {
    if (!this.props.show) {
      return (
        <div className="ReadSentenceArea">
          {this.props.data.map((obj) => (
            <Sentence key={obj.id} text={obj.text} />
          ))}
        </div>
      );
    } else {
      let sentenceList = [];
      for (let i = -1; this.props.data[i] || -i > this.props.show; i--) {
        sentenceList.prepend(<Sentence key={this.props.data[i].id} text={this.props.data[i].text} />);
      }
      return (
        <div className="ReadSentenceArea">
          {/* {this.props.data.map((obj) => (
            <Sentence key={obj.id} text={obj.text} />
          ))} */}
          {sentenceList}
        </div>
      );
    }
  }
}

export default ReadSentenceArea;
