import React, { Component } from "react";
import "./App.css";
// component
import SentenceBox from "./SentenceBox.js";
// import RecommendButton from "./RecommendButton.js";
// json
import _commentsJson from "./data/comments.json";
// function
import * as Myfunc from "./Myfunc.js";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      commentsJson: _commentsJson,
    };
  }
  render() {
    var comment_list = Myfunc.loadcommentsJsonToList(this.state.commentsJson);
    return (
      <div>
        <nav id="header">
          <div className="selectmode">독서</div>
          <div className="selectmode">참여하기</div>
          <div id="about">About</div>
        </nav>
        <div className="SentenceArea">{comment_list}</div>
      </div>
    );
  }
}

export default App;
