import React, { Component } from "react";
// component
import CommentBox from "./CommentBox.js";
import RecommendButton from "./RecommendButton.js";
// json
import commentsJson from "./data/comments.json";
// function
import * as Myfunc from "./Myfunc.js";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      _commentsJson: commentsJson,
    };
  }
  render() {
    var comment_list = Myfunc.loadcommentsJsonToList(_commentsJson);
    return <div className="App">{comment_list}</div>;
  }
}

export default App;
