import React, { Component } from "react";
// component
import CommentBox from "./CommentBox.js";
import RecommendButton from "./RecommendButton.js";
// json
import commentsJson from "./data/comments.json";
// function

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    var comment_list = [];
    for (var key in commentsJson) {
      comment_list.push(
        <div>
          <CommentBox isReadOnly={true} content={commentsJson[key].contents}></CommentBox>
          <RecommendButton></RecommendButton>
        </div>
      );
    }
    return <div className="App">{comment_list}</div>;
  }
}

export default App;
