import React, { Component } from "react";

class CommentBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      _isReadOnly: this.props.isReadonly, // true: 읽기전용
      _content: this.props.content, // 문장 내용
    };
  }
  render() {
    return <input type="text" defaultValue={this.state._content} readOnly={this.state._isReadOnly}></input>;
  }
}

export default CommentBox;
